#include <yuni/yuni.h>
#include <yuni/core/io/file.h>
#include <iostream>


int main(int argc, char** argv)
{
	if (argc <= 1)
		return 0;

	// We will read our file chunk by chunk (4K for each chunk)
	Yuni::CustomString<4096> buffer;

	// For each filename given from the command line
	for (int i = 1; i < argc; ++i)
	{
		// The filename to read
		const char* filename = argv[i];

		Yuni::Core::IO::File::Stream file;
		if (file.open(filename))
		{
			std::cout << "reading " << filename << std::endl;
			while (true)
			{
				// Trying to read the next chunk from the file
				if (!file.read(buffer))
					break;
				// Writing it to the standard output
				std::cout << buffer;
			}

			// We have reached the end of the file
			std::cout << std::endl;
		}
		else
			std::cout << "!! impossible to open " << filename << std::endl;
	}
	return 0;
}

#include <yuni/yuni.h>
#include <yuni/core/io/file.h>
#include <iostream>


int main(int argc, char** argv)
{
	if (argc <= 1)
		return 0;

	// We will read our file chunk by chunk (4K for each chunk)
	Yuni::CustomString<4096> line;

	for (int i = 1; i < argc; ++i)
	{
		// The filename to read
		const char* filename = argv[i];

		Yuni::Core::IO::File::Stream file;
		if (file.open(filename))
		{
			std::cout << "reading " << filename << std::endl;
			unsigned int line = 0;
			while (true)
			{
				++line;
				// Trying to read the next line
				if (!file.readline(buffer))
					break;
				// Writing the line to the standard output
				std::cout << line << ": " << buffer;
			}

			// We have reached the end of the file
			std::cout << std::endl;
		}
		else
			std::cout << "!! impossible to open " << filename << std::endl;
	}
	return 0;
}

#include <yuni/yuni.h>
#include <yuni/core/io/file.h>
#include <iostream>


int main(int argc, char** argv)
{
	if (argv <= 1)
		return 0;

	// The filename where to write some data
	const char* filename = argv[1];

	Yuni::Core::IO::File::Stream file;
	// Trying to open the filename in read/write mode. If the file exists it will
	// truncated.
	if (file.openRW(filename))
	{
		file << "Hello world !\n";
	}
	else
		std::cout << "impossible to write " << filename << std::endl;
	return 0;
}
