div:
This class is reentrant. Consequently, it will be thread-safe as long as the user custom implementation is thread-safe.
  #include  < yuni/yuni.h >
  #include  < yuni/io/directory/iterator.h >
  #include  <iostream>

  using  namespace  Yuni;


  class  MyIterator  :  public  IO::Directory:: IIterator <true>
  {
  public :
     //!  Flow
     typedef  IO::Flow  Flow ;

  public :
     MyIterator()  {}
     virtual  ~MyIterator()
     {
         //  For  code  robustness  and  to  avoid  corrupt  vtable
         stop ();
     }

  protected :
     virtual  bool  onStart ( const  String &  rootFolder)
     {
         std::cout  <<  "  [+]  "  <<  rootFolder  <<  std::endl;
         pCounter  =  1;
         pFileCount  =  0;
         pFolderCount  =  0;
         pTotalSize  =  0;
         return  true ;
     }

     virtual  Flow  onBeginFolder ( const  String &,  const  String &,  const  String &  name)
     {
         printSpaces();
         std::cout  <<  "  [+]  "  <<  name  <<  std::endl;
         ++pCounter;
         ++pFolderCount;
         return  IO::flowContinue ;
     }

     virtual  void  onEndFolder ( const  String &,  const  String &,  const  String &)
     {
         --pCounter;
     }

     virtual  Flow  onFile ( const  String &,  const  String &,  const  String &  name,  uint64  size)
     {
         printSpaces();
         std::cout  <<  "   -   "  <<  name  <<  "  ("  <<  size  <<  "  bytes)"  <<  std::endl;
         ++pFileCount;
         pTotalSize  +=  size;
         return  IO::flowContinue ;
     }

     virtual  void  onTerminate ()
     {
         std::cout  <<  "\n" ;
         std::cout  <<  pFolderCount  <<  "  folder(s),  "  <<  pFileCount  <<  "  file(s),   "
             <<  pTotalSize  <<  "  bytes"  <<  std::endl;
     }

  private :
     void  printSpaces()
     {
         for  ( unsigned  int  i  =  0;  i  !=  pCounter;  ++i)
             std::cout  <<  "     " ;
     }

  private :
     unsigned  int  pCounter;
     unsigned  int  pFolderCount;
     unsigned  int  pFileCount;
     uint64  pTotalSize;
  };


  int  main()
  {
         MyIterator  iterator;
         iterator.add( "/tmp" );
         iterator.start();
         iterator.wait();
         return  0;
  }


 This class is thread-safe even when not in detached mode.
Parameters :
 DetachedT True to perform the operation into a separate thread

