#include  <yuni/static/inherit.h>
#include  <iostream>

class  Base  {};

class  A  :  public  Base  {};

class  B  {};

int  main()
{
   std::cout  <<  "A  inherits  from  Base  :"  <<  Yuni::Static::InheritsFrom<A,Base>::Yes  <<  "\n" ;
   std::cout  <<  "B  inherits  from  Base  :"  <<  Yuni::Static::InheritsFrom<B,Base>::Yes  <<  "\n" ;
   return  0;
}
